          <el-input v-model="data.origin" placeholder="请输入原密码" show-password />
        </el-form-item>
        <el-form-item label="新密码:" prop="new">
          <el-input v-model="data.new" placeholder="请输入新密码" show-password />
        </el-form-item>
        <el-form-item label="确认密码:" prop="confirm">
          <el-input v-model="data.confirm" placeholder="请输入确认密码" show-password />
