<script>
import axios from "../axios.js";
import store from "../store/index";
export default {
  name: "Header",
  data() {
    return {
      state: sessionStorage.username == "admin" ? true : false,
      username: "",
      isCollapse: false,
      isActive: false,
      rules: {
        origin: [{ required: true, message: "请输入原密码", trigger: "blur" }],
        new: [
          { required: true, message: "请输入新密码", trigger: "blur" },
          {
            pattern: /^(?=.*[A-Za-z])(?=.*\d)(?=.*[$@$!%*#?&])[A-Za-z\d$@$!%*#?&]{8,}$/,
            message: "至少8个字符，必须包含1个字母，1个数字和1个特殊字符"
          }
        ],
        confirm: [
          { required: true, message: "请输入确认密码", trigger: "blur" },
          {
            pattern: /^(?=.*[A-Za-z])(?=.*\d)(?=.*[$@$!%*#?&])[A-Za-z\d$@$!%*#?&]{8,}$/,
            message: "至少8个字符，必须包含1个字母，1个数字和1个特殊字符"
          }
        ]
      },
      data: {
        origin: "",
        new: "",
        confirm: ""
      },
      visible: false
    };
  },
  created() {
    this.username = sessionStorage.username;
  },
  mounted() {},
  methods: {
    close() {
      this.isCollapse = true;
      this.isActive = true;
    },
    open() {
      this.isCollapse = false;
      this.isActive = false;
    },
    handleEdit() {
      this.visible = true;
    },
    modifyPassword() {
      var _this = this;
      this.$refs["dataForm"].validate(valid => {
        if (valid) {
          let data = {
            username: _this.username,
            origin: _this.data.origin,
            new: _this.data.new
          };
          if (_this.data.new === _this.data.confirm) {
            axios.UpdatePassword(data).then(res => {
              if (res.data.code === 200) {
                this.resetStatus();
                this.$message({
                  message: "修改成功",
                  type: "success"
                });
              } else {
                this.$message({
                  message: res.data.msg,
                  type: "error"
                });
              }
            });
          } else {
            this.$message({
              message: "密码不一致",
              type: "error"
            });
          }
        }
      });
    },
    resetStatus() {
      this.visible = false;
    },
    //登出
    logout() {
      //清除token
      var _this = this;
      this.$confirm("确认退出吗?", "提示", {
        type: "warning"
      }).then(() => {
        store.dispatch("UserLogout");
        if (!store.state.token) {
          _this.$router.push("/");
          _this.$message({
            type: "success",
            message: "注销成功"
          });
        } else {
          _this.$message({
            type: "info",
            message: "注销失败"
          });
        }
      });
    }
  }
};
</script>
